CREATE TRIGGER trg_CheckMammalType\nON Mammals\nINSTEAD OF INSERT\nAS\nBEGIN\n    IF EXISTS (\n        SELECT 1\n        FROM inserted i\n        JOIN Animals a ON i.AnimalId = a.AnimalId\n        WHERE a.AnimalType <> 1\n    )\n    BEGIN\n        RAISERROR ('Only mammals can be inserted into the Mammals table.', 16, 1);\n        ROLLBACK TRANSACTION;\n        RETURN;\n    END\n    \n    -- Insert the rows if the condition is met\n    INSERT INTO Mammals (AnimalId, NumberOfMammaryGlads)\n    SELECT AnimalId, NumberOfMammaryGlads\n    FROM inserted;\nEND;