-- Query to find duplicates based on CompanyName, CompanyCity, and CompanyState\nSELECT CompanyName, CompanyCity, CompanyState, COUNT(*)\nFROM Company\nGROUP BY CompanyName, CompanyCity, CompanyState\nHAVING COUNT(*) > 1\n\nUNION ALL\n\n-- Query to find duplicates based on CompanyAddress, CompanyCity, and CompanyState\nSELECT CompanyAddress, CompanyCity, CompanyState, COUNT(*)\nFROM Company\nGROUP BY CompanyAddress, CompanyCity, CompanyState\nHAVING COUNT(*) > 1