-- Query to find detailed duplicates based on CompanyName, CompanyCity, and CompanyState\nSELECT *\nFROM Company c1\nWHERE EXISTS (\n    SELECT 1\n    FROM Company c2\n    WHERE c1.CompanyName = c2.CompanyName\n      AND c1.CompanyCity = c2.CompanyCity\n      AND c1.CompanyState = c2.CompanyState\n      AND c1.CompanyId <> c2.CompanyId\n)\n\nUNION ALL\n\n-- Query to find detailed duplicates based on CompanyAddress, CompanyCity, and CompanyState\nSELECT *\nFROM Company c1\nWHERE EXISTS (\n    SELECT 1\n    FROM Company c2\n    WHERE c1.CompanyAddress = c2.CompanyAddress\n      AND c1.CompanyCity = c2.CompanyCity\n      AND c1.CompanyState = c2.CompanyState\n      AND c1.CompanyId <> c2.CompanyId\n)