-- Search in procedures, functions, packages, triggers, views for the current user\nSELECT name, type, line, text\nFROM user_source\nWHERE UPPER(text) LIKE UPPER('%search string%');\n\n-- Search in views for the current user\nSELECT view_name, text\nFROM user_views\nWHERE UPPER(text) LIKE UPPER('%search string%');\n\n-- Search in table and column names for the current user\nSELECT table_name, column_name\nFROM user_tab_columns\nWHERE UPPER(table_name) LIKE UPPER('%search string%')\n   OR UPPER(column_name) LIKE UPPER('%search string%');