SELECT COUPONS.DocType, COUPONS.PayTo, COUPONS.ContactName, COUPONS.ContactNumber,\n       COUPONS.DocFooter, COUPONS.PQBName, COUPONS.LetterDate, COUPONS.RetireeFirstName, \n       COUPONS.RetireeLastName, COUPONS.Address1, COUPONS.Address2, COUPONS.City, \n       COUPONS.State, COUPONS.ZIP, COUPONS.PQBSSN, COUPONS.EmployerCode, COUPONS.AmountDue, \n       COUPONS.DateDue\nFROM COUPONS\nWHERE COUPONS.DateDue BETWEEN #01/01/2009# AND #12/01/2009#\nAND COUPONS.DateDue IN (\n    SELECT TOP 6 SubCOUPONS.DateDue\n    FROM COUPONS AS SubCOUPONS\n    WHERE SubCOUPONS.DocType = COUPONS.DocType\n    AND SubCOUPONS.PayTo = COUPONS.PayTo\n    AND SubCOUPONS.ContactName = COUPONS.ContactName\n    AND SubCOUPONS.ContactNumber = COUPONS.ContactNumber\n    AND SubCOUPONS.DateDue BETWEEN #01/01/2009# AND #12/01/2009#\n    ORDER BY Right([SubCOUPONS.DateDue], 4) + Left([SubCOUPONS.DateDue], 2) + Mid([SubCOUPONS.DateDue], 4, 2)\n)\nORDER BY COUPONS.DocType, COUPONS.PayTo, COUPONS.ContactName, COUPONS.ContactNumber, \n         COUPONS.DocFooter, COUPONS.PQBName, COUPONS.LetterDate, \n         Right([DateDue], 4) + Left([DateDue], 2) + Mid([DateDue], 4, 2);