-- Create a logging table\nCREATE TABLE AuditLog (\n    AuditID INT IDENTITY(1,1) PRIMARY KEY,\n    TableName NVARCHAR(255),\n    Operation NVARCHAR(50),\n    Timestamp DATETIME DEFAULT GETDATE()\n);\n\n-- Create a trigger for INSERT\nCREATE TRIGGER trgAfterInsert ON YourTable\nAFTER INSERT\nAS\nBEGIN\n    INSERT INTO AuditLog (TableName, Operation)\n    VALUES ('YourTable', 'INSERT');\nEND;\nGO\n\n-- Create a trigger for UPDATE\nCREATE TRIGGER trgAfterUpdate ON YourTable\nAFTER UPDATE\nAS\nBEGIN\n    INSERT INTO AuditLog (TableName, Operation)\n    VALUES ('YourTable', 'UPDATE');\nEND;\nGO