DELIMITER //\n\nCREATE PROCEDURE check_and_abort()\nBEGIN\n    DECLARE id_value INT;\n\n    -- Example of a SELECT statement that may set a variable to NULL\n    SELECT id INTO id_value FROM your_table WHERE some_condition;\n\n    -- Check if the value is NULL\n    IF id_value IS NULL THEN\n        -- Raise an error\n        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Required value not found, aborting script execution';\n    END IF;\nEND //\n\nDELIMITER ;\n\n-- Call the procedure at the point you want to perform the check\nCALL check_and_abort();