-- Create a temporary table with the same structure as the result set of the stored procedure\nCREATE TABLE #TempTable (\n    TABLE_QUALIFIER sysname,\n    TABLE_OWNER sysname,\n    TABLE_NAME sysname,\n    COLUMN_NAME sysname,\n    DATA_TYPE smallint,\n    TYPE_NAME sysname,\n    PRECISION int,\n    LENGTH int,\n    SCALE smallint,\n    RADIX smallint,\n    NULLABLE smallint,\n    REMARKS nvarchar(254),\n    COLUMN_DEF nvarchar(4000),\n    SQL_DATA_TYPE smallint,\n    SQL_DATETIME_SUB smallint,\n    CHAR_OCTET_LENGTH int,\n    ORDINAL_POSITION int,\n    IS_NULLABLE nvarchar(254),\n    SS_DATA_TYPE smallint\n);\n\n-- Insert the result of the stored procedure into the temporary table\nINSERT INTO #TempTable\nEXEC sp_columns 'tblSomeTableName';\n\n-- Query the temporary table\nSELECT COLUMN_NAME\nFROM #TempTable;\n\n-- Clean up\nDROP TABLE #TempTable;