DECLARE @foo INT\nSET @foo = NULL\n\n-- Create the temp table first\nCREATE TABLE #TempTable (Message VARCHAR(50))\n\n-- Populate the temp table based on the condition\nIF (@foo IS NULL)\nBEGIN\n    INSERT INTO #TempTable (Message)\n    VALUES ('foo was null')\nEND\nELSE\nBEGIN\n    INSERT INTO #TempTable (Message)\n    VALUES ('foo not null')\nEND\n\n-- Select from the temp table to see the results\nSELECT * FROM #TempTable\n\n-- Drop the temp table when done\nDROP TABLE #TempTable