SELECT Name, Value\nFROM (\n    SELECT Name, Value,\n           ROW_NUMBER() OVER (ORDER BY Value) AS rn,\n           ROW_NUMBER() OVER (ORDER BY Value DESC) AS rn_desc\n    FROM your_table\n    WHERE Value > :query_param\n    UNION ALL\n    SELECT Name, Value,\n           NULL, 1\n    FROM your_table\n    ORDER BY Value DESC\n    FETCH FIRST ROW ONLY\n)\nWHERE rn = 1 OR rn_desc = 1\nORDER BY rn\nFETCH FIRST ROW ONLY;