DECLARE @searchString NVARCHAR(100) = 'misspelled_string';\nDECLARE @sql NVARCHAR(MAX) = '';\n\nSELECT @sql = @sql + 'SELECT ''' + TABLE_NAME + ''' AS TableName, ''' + COLUMN_NAME + ''' AS ColumnName, ' +\n               '[' + COLUMN_NAME + ']' + ' AS ColumnValue ' +\n               'FROM [' + TABLE_SCHEMA + '].[' + TABLE_NAME + '] ' +\n               'WHERE [' + COLUMN_NAME + '] LIKE ''%' + @searchString + '%'' UNION '\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE DATA_TYPE IN ('varchar', 'text', 'nvarchar', 'ntext');\n\n-- Remove the last UNION\nIF LEN(@sql) > 0\n    SET @sql = LEFT(@sql, LEN(@sql) - 6);\n\n-- Execute the dynamic SQL\nEXEC sp_executesql @sql;