-- Create the source table\nCREATE TABLE ExamAnswers\n(\n    StudentID varchar(12),\n    QuestionID int,\n    Answer char(1)\n);\n\n-- Insert example data\nINSERT INTO ExamAnswers (StudentID, QuestionID, Answer)\nVALUES \n('Bob', 1, 'a'),\n('Bob', 2, 'c'),\n-- ... (other data)\n('Bob', 100, 'b'),\n('Chris', 1, 'c'),\n('Chris', 2, 'd'),\n-- ... (other data)\n('Chris', 100, null);\n\n-- Query to pivot the table\nSELECT StudentID, [1], [2], -- ..., [100]\nFROM \n(\n    SELECT StudentID, QuestionID, Answer\n    FROM ExamAnswers\n) AS SourceTable\nPIVOT\n(\n    MAX(Answer)\n    FOR QuestionID IN ([1], [2], -- ..., [100])\n) AS PivotTable;