SELECT p.*\nFROM Projects p\nLEFT JOIN Roofing r ON p.projectID = r.projectID\nLEFT JOIN Siding s ON p.projectID = s.projectID\nLEFT JOIN Gutters g ON p.projectID = g.projectID\nLEFT JOIN Misc m ON p.projectID = m.projectID\nWHERE (r.status IS NULL OR r.status = 'completed')\n  AND (s.status IS NULL OR s.status = 'completed')\n  AND (g.status IS NULL OR g.status = 'completed')\n  AND (m.status IS NULL OR m.status = 'completed');