CREATE TRIGGER AccountChecker\nON student\nAFTER DELETE\nAS\nBEGIN\n    DECLARE @acc CHAR(4);\n    \n    IF EXISTS (SELECT 1 FROM deleted)\n    BEGIN\n        SELECT @acc = account.account_number \n        FROM account\n        WHERE account.student_id = (SELECT student_id FROM deleted);\n\n        IF @acc IS NOT NULL\n        BEGIN\n            RAISERROR 99999 'Cannot delete student record as there are related accounts.'\n        END\n    END\nEND