-- Install tSQLt framework first\nEXEC tSQLt.NewTestClass 'TestExample';\n\n-- Create a test for a stored procedure\nCREATE PROCEDURE TestExample.[test stored procedure returns correct result]\nAS\nBEGIN\n    -- Arrange\n    EXEC tSQLt.FakeTable 'YourTable';\n    INSERT INTO YourTable (Column1, Column2) VALUES ('Value1', 'Value2');\n\n    -- Act\n    EXEC YourStoredProcedure @Param1 = 'Value1';\n\n    -- Assert\n    SELECT @Expected = 'ExpectedResult';\n    EXEC tSQLt.AssertEquals @Expected, @Actual;\nEND;\nGO\n\n-- Run the test\nEXEC tSQLt.Run 'TestExample';