SELECT\n  SUM(CASE\n        WHEN datetime >= $P{oneDayAgo} AND datetime <= $P{now} THEN count\n        ELSE 0\n      END) AS today,\n  SUM(CASE\n        WHEN datetime >= $P{oneWeekAgo} AND datetime <= $P{now} THEN count\n        ELSE 0\n      END) AS thisWeek,\n  SUM(CASE\n        WHEN datetime >= $P{oneMonthAgo} AND datetime <= $P{now} THEN count\n        ELSE 0\n      END) AS thisMonth\nFROM\n  statistics\nWHERE\n  name = 'test'