-- Find the right value of the parent node\nDECLARE @ParentRight INT;\nSELECT @ParentRight = RightValue FROM Tree WHERE NodeID = @ParentID;\n\n-- Update the right values of nodes\nUPDATE Tree SET RightValue = RightValue + 2 WHERE RightValue >= @ParentRight;\nUPDATE Tree SET LeftValue = LeftValue + 2 WHERE LeftValue > @ParentRight;\n\n-- Insert the new node\nINSERT INTO Tree (NodeID, NodeName, LeftValue, RightValue)\nVALUES (@NodeID, @NodeName, @ParentRight, @ParentRight + 1);