CREATE TRIGGER trg_UpdateTable\nON YourTable\nAFTER UPDATE\nAS\nBEGIN\n    IF (SESSION_CONTEXT(N'TriggerFired') IS NULL)\n    BEGIN\n        -- Set the context to indicate the trigger has fired\n        EXEC sp_set_session_context @key = N'TriggerFired', @value = 1;\n\n        -- Your update logic here\n        UPDATE YourTable\n        SET YourColumn = 'NewValue'\n        WHERE SomeCondition = 'Condition';\n\n        -- Reset the context variable if needed\n        -- EXEC sp_set_session_context @key = N'TriggerFired', @value = NULL;\n    END\nEND