CREATE TABLE pages (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    title VARCHAR(255) NOT NULL,\n    current_revision_id INT,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\nCREATE TABLE revisions (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    page_id INT,\n    content TEXT,\n    author VARCHAR(255),\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (page_id) REFERENCES pages(id)\n);