SELECT [columns]\nFROM [table]\nWHERE (\n    3963.191 * \n    ACOS(\n        SIN(PI() * [latitude] / 180) * SIN(PI() * [table].latitude / 180) + \n        COS(PI() * [latitude] /180) * COS(PI() * [table].latitude / 180) * COS(PI() * [table].longitude / 180 - PI() * [longitude] / 180)\n    ) \n    <= 10\n) OR (\n    3963.191 * \n    ACOS(\n        SIN(PI() * [latitude] / 180) * SIN(PI() * [table].latitude / 180) + \n        COS(PI() * [latitude] /180) * COS(PI() * [table].latitude / 180) * COS(PI() * [table].longitude / 180 - PI() * [longitude] / 180)\n    ) \n    <= ([table].service_range + 10)\n)