DECLARE\n    CURSOR c IS\n        SELECT blah1, blah2\n        FROM your_table\n        WHERE your_conditions;\n\n    var1_ your_table.blah1%TYPE;\n    var2_ your_table.blah2%TYPE;\nBEGIN\n    OPEN c;\n    FETCH c INTO var1_, var2_;\n    \n    IF c%NOTFOUND THEN\n        -- No rows found, do nothing or assign default values if needed\n        var1_ := var1_; -- No value change\n        var2_ := var2_; -- No value change\n    ELSE\n        -- Fetch remaining rows if any, and use the last row's values\n        WHILE c%FOUND LOOP\n            FETCH c INTO var1_, var2_;\n        END LOOP;\n    END IF;\n\n    CLOSE c;\nEXCEPTION\n    WHEN OTHERS THEN\n        -- Handle other exceptions if necessary\n        CLOSE c;\n        RAISE;\nEND;\n/