-- Example of a transaction using COMMIT\nBEGIN TRANSACTION;\n\n-- Update statement\nUPDATE MyTable SET Column1 = 'Value1' WHERE Column2 = 'Value2';\n\n-- Commit the transaction\nCOMMIT;\n\n-- Example of using CHECKPOINT\nBEGIN TRANSACTION;\n\n-- Update statement\nUPDATE MyTable SET Column1 = 'Value1' WHERE Column2 = 'Value2';\n\n-- This will not commit the transaction\nCHECKPOINT;\n\n-- Commit the transaction\nCOMMIT;