-- Calculate the Wilson Score Interval\nSELECT \n    id,\n    title,\n    rating_sum / rating_count * 100 AS absolute_rating,\n    rating_count,\n    ( (rating_sum / rating_count * 1.0 * (rating_sum + rating_count) + 1.96 * 1.96 / (2 * rating_count)) - 1.96 * sqrt((rating_sum * (rating_count - rating_sum) + 1.96 * 1.96 / 4) / rating_count) ) / (rating_count + 1.96 * 1.96) AS wilson_score\nFROM \n    news\nWHERE \n    rating_count > 0\nORDER BY \n    wilson_score DESC\nLIMIT 5;