WITH RECURSIVE Tree AS (\n    SELECT id, parentid, data1, data2, /* ..., dataN */\n    FROM your_table\n    WHERE id = @root_id -- Replace @root_id with the id of the node you are focusing on\n\n    UNION ALL\n\n    SELECT child.id, child.parentid, child.data1, child.data2, /* ..., child.dataN */\n    FROM your_table child\n    INNER JOIN Tree parent ON child.parentid = parent.id\n)\nSELECT SUM(data1) AS sum_data1,\n       SUM(data2) AS sum_data2\n       /* ..., SUM(dataN) AS sum_dataN */\nFROM Tree;