-- To find units in DB1.TableA that are not in DB2.TableB\nSELECT a.unitNo, a.BuildingNo\nFROM DB1.dbo.TableA a\nLEFT JOIN DB2.dbo.TableB b ON a.unitNo = b.unitNo AND a.BuildingNo = b.BuildingNo\nWHERE b.unitNo IS NULL;\n\n-- To find units in DB2.TableB that are not in DB1.TableA\nSELECT b.unitNo, b.BuildingNo\nFROM DB2.dbo.TableB b\nLEFT JOIN DB1.dbo.TableA a ON b.unitNo = a.unitNo AND b.BuildingNo = a.BuildingNo\nWHERE a.unitNo IS NULL;