UPDATE history_table\nSET some_date_col = \n    TO_CHAR(\n        CASE\n            WHEN REGEXP_LIKE(some_date_col, '^\d{4}-\d{2}-\d{2}$') THEN TO_DATE(some_date_col, 'yyyy-mm-dd') + 30\n            WHEN REGEXP_LIKE(some_date_col, '^\d{2}/\d{2}/\d{4}$') THEN TO_DATE(some_date_col, 'mm/dd/yyyy') + 30\n            WHEN REGEXP_LIKE(some_date_col, '^\d{2}/\d{2}/\d{2}$') THEN\n                CASE\n                    WHEN SUBSTR(some_date_col, 7, 2) > '50' THEN TO_DATE(some_date_col, 'mm/dd/yy') + 30\n                    ELSE TO_DATE(some_date_col, 'dd/mm/yy') + 30\n                END\n            WHEN REGEXP_LIKE(some_date_col, '^\d{2}/\d{2}/\d{4}$') THEN TO_DATE(some_date_col, 'dd/mm/yyyy') + 30\n            ELSE NULL\n        END,\n    'mm/dd/yyyy')\nWHERE some_date_col IS NOT NULL;