SELECT \n    CASE \n        WHEN t.freeplayabandoned != f.freeplayabandoned THEN 'freeplayabandoned'\n        WHEN t.freeplaydownloaded != f.freeplaydownloaded THEN 'freeplaydownloaded'\n    END AS mismatched_column\nFROM testtable t\nLEFT JOIN freeplay.egmfreeplay f\n    ON t.freeplaydownloaded = f.freeplaydownloaded\nWHERE f.freeplaydownloaded IS NULL \n    OR t.freeplayabandoned != f.freeplayabandoned\n    OR t.freeplaydownloaded != f.freeplaydownloaded;