EXPLAIN SELECT a.user, pp.courriel\nFROM acces a\nJOIN profil_perso pp ON pp.id = a.perso_id\nJOIN acces_profil_sport ap ON ap.id = a.id\nWHERE EXISTS (\n    SELECT 1\n    FROM activite_sport ac\n    JOIN activite act ON act.activite_id = ac.activite_id\n    WHERE ac.sport_id = ap.sport_id AND act.is_cron = 1 AND act.cron_processed = 0\n)\nAND EXISTS (\n    SELECT 1\n    FROM activite_region ar\n    JOIN activite act ON act.activite_id = ar.activite_id\n    WHERE ar.region_id = pp.region_id AND act.is_cron = 1 AND act.cron_processed = 0\n)\nGROUP BY a.id, pp.courriel;