DECLARE @msg XML\nSET @msg = '<root><date>2009-05-09T13:50:59.6361485+08:00</date></root>'\n\n-- Extract the datetime with timezone as datetimeoffset\nDECLARE @datetimeoffset datetimeoffset\nSET @datetimeoffset = @msg.value(N'(//root/date/text())[1]', 'datetimeoffset')\n\n-- Convert datetimeoffset to datetime\nDECLARE @datetime datetime\nSET @datetime = CAST(@datetimeoffset AS datetime)\n\n-- Select the converted datetime\nSELECT @datetime AS [ConvertedDateTime]