-- Trigger for INSERT on Store table\n    DELIMITER //\n    CREATE TRIGGER after_store_insert\n    AFTER INSERT ON Store\n    FOR EACH ROW\n    BEGIN\n        INSERT INTO ChangeLog (user_id, action, table_name, record_id, changes)\n        VALUES (NEW.user_id, 'INSERT', 'Store', NEW.id, CONCAT('Store created with ID: ', NEW.id));\n    END;\n    //\n    DELIMITER ;\n\n    -- Trigger for UPDATE on Store table\n    DELIMITER //\n    CREATE TRIGGER after_store_update\n    AFTER UPDATE ON Store\n    FOR EACH ROW\n    BEGIN\n        INSERT INTO ChangeLog (user_id, action, table_name, record_id, changes)\n        VALUES (NEW.user_id, 'UPDATE', 'Store', NEW.id, CONCAT('Store updated with ID: ', NEW.id));\n    END;\n    //\n    DELIMITER ;\n\n    -- Trigger for DELETE on Store table\n    DELIMITER //\n    CREATE TRIGGER after_store_delete\n    AFTER DELETE ON Store\n    FOR EACH ROW\n    BEGIN\n        INSERT INTO ChangeLog (user_id, action, table_name, record_id, changes)\n        VALUES (OLD.user_id, 'DELETE', 'Store', OLD.id, CONCAT('Store deleted with ID: ', OLD.id));\n    END;\n    //\n    DELIMITER ;