SELECT \n    xsc.name AS SchemaCollectionName,\n    xsn.name AS Namespace,\n    xsn.xml_schema_namespace_id,\n    xsn.xml_schema_id,\n    CAST(xsn.xml_component as XML) AS SchemaDefinition\nFROM \n    sys.xml_schema_collections xsc\nINNER JOIN \n    sys.xml_schema_namespaces xsn \nON \n    xsc.xml_collection_id = xsn.xml_collection_id\nWHERE \n    xsc.name = 'YourSchemaCollectionName';