-- Enable FILESTREAM at the instance level\nEXEC sp_configure filestream_access_level, 2;\nRECONFIGURE;\n\n-- Create a database with FILESTREAM\nCREATE DATABASE FileStreamDB\nON PRIMARY\n(\n    NAME = FileStreamDB_Data,\n    FILENAME = 'C:\Path\To\FileStreamDB_Data.mdf'\n),\nFILEGROUP FileStreamGroup CONTAINS FILESTREAM\n(\n    NAME = FileStreamDB_FileStream,\n    FILENAME = 'C:\Path\To\FileStream'\n)\nLOG ON\n(\n    NAME = FileStreamDB_Log,\n    FILENAME = 'C:\Path\To\FileStreamDB_Log.ldf'\n);\nGO\n\n-- Create a table with a FILESTREAM column\nCREATE TABLE DocumentStore (\n    DocumentID UNIQUEIDENTIFIER ROWGUIDCOL NOT NULL UNIQUE DEFAULT NEWID(),\n    Document VARBINARY(MAX) FILESTREAM NULL,\n    DocumentName NVARCHAR(255) NOT NULL\n);\nGO\n\n-- Insert an image into the table\nINSERT INTO DocumentStore (DocumentName, Document)\nVALUES ('ExampleImage', CAST('' AS VARBINARY(MAX)));\nGO