SELECT\n  Page.*,\n  COUNT(DISTINCT PageView.id) AS views,\n  COUNT(DISTINCT PageItem.id) AS items,\n  COALESCE(SUM(PageVote.vote), 0) AS votes\nFROM pages AS Page\nLEFT JOIN page_views AS PageView\n  ON Page.id = PageView.page_id\nLEFT JOIN page_items AS PageItem\n  ON Page.id = PageItem.page_id\nLEFT JOIN page_votes AS PageVote\n  ON Page.id = PageVote.page_id\nGROUP BY Page.id\nORDER BY views DESC\nLIMIT 10 OFFSET 0;