-- Create a FULLTEXT index on the column containing the text data\nALTER TABLE your_table ADD FULLTEXT(text_column);\n\n-- Query to find the most repeated words\nSELECT word, COUNT(*) AS occurrences\nFROM (\n    SELECT word\n    FROM your_table\n    CROSS JOIN (\n        SELECT word FROM (\n            SELECT 'YOUR STOPWORDS LIST' AS stopword\n        ) stopwords\n        WHERE FIND_IN_SET(word, stopwords) = 0\n    ) filtered_words\n    WHERE MATCH(text_column) AGAINST (word IN NATURAL LANGUAGE MODE)\n) repeated_words\nGROUP BY word\nORDER BY occurrences DESC\nLIMIT 10;