-- test.sql\n\n-- Check if all parameters are provided\nDEFINE userid = '&1'\nDEFINE pw = '&2'\nDEFINE tablespace = '&3'\n\n-- Check for missing parameters\nBEGIN\n    IF '&1' IS NULL THEN\n        DBMS_OUTPUT.PUT_LINE('Error: Missing parameter userid.');\n        RAISE_APPLICATION_ERROR(-20001, 'Missing parameter userid.');\n    ELSIF '&2' IS NULL THEN\n        DBMS_OUTPUT.PUT_LINE('Error: Missing parameter pw.');\n        RAISE_APPLICATION_ERROR(-20002, 'Missing parameter pw.');\n    ELSIF '&3' IS NULL THEN\n        DBMS_OUTPUT.PUT_LINE('Error: Missing parameter tablespace.');\n        RAISE_APPLICATION_ERROR(-20003, 'Missing parameter tablespace.');\n    END IF;\nEND;\n/\n\n-- Continue with your script logic here\n-- ...\n\n-- Exit with success code if all parameters are provided\nEXIT SUCCESS;