SELECT \n    id,\n    title,\n    LEAST(article_release_date, issue_release_date) AS release_date\nFROM \n    (\n        SELECT \n            a.id, \n            a.title, \n            a.release_date AS article_release_date, \n            i.release_date AS issue_release_date\n        FROM \n            articles a\n        LEFT JOIN \n            issues i ON a.issue_id = i.id\n    ) AS combined\nORDER BY \n    release_date;