SELECT\n    t.name AS tag_name,\n    COUNT(DISTINCT COALESCE(tc.blog_post_id, tb.blog_post_id)) AS blog_post_count\nFROM\n    Tags t\nLEFT JOIN\n    Taggings tg ON t.id = tg.tag_id\nLEFT JOIN\n    BlogPosts tb ON tg.blog_post_id = tb.id\nLEFT JOIN\n    BlogComments tc ON tg.blog_comment_id = tc.id\nGROUP BY\n    t.name\nORDER BY\n    blog_post_count DESC\nLIMIT n;  -- Replace 'n' with the number of top popular tags you want to retrieve.