-- Declare variables\nDECLARE @COUNTER INT\nDECLARE @TOTAL INT\nDECLARE @TABLE NVARCHAR(128)\nDECLARE @COLUMN NVARCHAR(128)\nDECLARE @COLUMNTYPE NVARCHAR(128)\nDECLARE @COLUMNVALUE INT\nDECLARE @SQL NVARCHAR(4000)\n\n-- What we are looking for\nSET @COLUMNTYPE = 'INT'\nSET @COLUMNVALUE = 3\nSET @COUNTER = 0\n\n-- Create a temporary table to hold table and column names\nCREATE TABLE #ColumnsToSearch (\n    TABLE_NAME NVARCHAR(128),\n    COLUMN_NAME NVARCHAR(128)\n)\n\n-- Insert table and column names of the specified data type into the temporary table\nINSERT INTO #ColumnsToSearch (TABLE_NAME, COLUMN_NAME)\nSELECT Columns.TABLE_NAME, Columns.COLUMN_NAME\nFROM INFORMATION_SCHEMA.Columns AS Columns \nJOIN INFORMATION_SCHEMA.TABLES AS Tables \nON Columns.TABLE_NAME = Tables.TABLE_NAME\nWHERE Columns.DATA_TYPE = @COLUMNTYPE\n\n-- Get the total number of columns to search\nSELECT @TOTAL = COUNT(*) FROM #ColumnsToSearch\n\n-- Search each table and column for the specified value\nWHILE @COUNTER < @TOTAL\nBEGIN\n    SET @COUNTER = @COUNTER + 1\n    \n    -- Fetch the next table and column name\n    SELECT @TABLE = TABLE_NAME, @COLUMN = COLUMN_NAME\n    FROM #ColumnsToSearch\n    WHERE ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) = @COUNTER\n    \n    -- Construct the SQL query\n    SET @SQL = 'IF EXISTS (SELECT 1 FROM ' + QUOTENAME(@TABLE) + ' WHERE ' + QUOTENAME(@COLUMN) + ' = ' + CAST(@COLUMNVALUE AS NVARCHAR) + ') ' +\n               'PRINT ''Found in table: ' + @TABLE + ', column: ' + @COLUMN + ''''\n\n    -- Execute the SQL query\n    EXEC sp_executesql @SQL\nEND\n\n-- Clean up\nDROP TABLE #ColumnsToSearch