DELIMITER //\n\nCREATE TRIGGER trg_PreventMultiplesOf13\nBEFORE INSERT ON Comments\nFOR EACH ROW\nBEGIN\n    DECLARE next_id INT;\n\n    -- Get the next auto-increment value\n    SET next_id = (SELECT AUTO_INCREMENT FROM information_schema.TABLES\n                   WHERE TABLE_SCHEMA = DATABASE()\n                   AND TABLE_NAME = 'Comments');\n\n    -- Check if the next ID is a multiple of 13\n    IF next_id % 13 = 0 THEN\n        -- Increment the auto-increment value to the next non-multiple of 13\n        SET next_id = next_id + 1;\n        WHILE next_id % 13 = 0 DO\n            SET next_id = next_id + 1;\n        END WHILE;\n\n        -- Set the new ID value\n        SET NEW.ID = next_id;\n\n        -- Update the auto-increment value\n        SET @alter_stmt = CONCAT('ALTER TABLE Comments AUTO_INCREMENT = ', next_id + 1);\n        PREPARE stmt FROM @alter_stmt;\n        EXECUTE stmt;\n        DEALLOCATE PREPARE stmt;\n    END IF;\nEND;\n//\n\nDELIMITER ;