CREATE TRIGGER trg_PreventMultiplesOf13\nON Comments\nAFTER INSERT\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @ID int;\n\n    SELECT @ID = ID\n    FROM inserted;\n\n    IF (@ID % 13 = 0)\n    BEGIN\n        -- Find the next non-multiple of 13 and update the ID\n        DECLARE @NewID int;\n        SET @NewID = @ID + 1;\n\n        WHILE (@NewID % 13 = 0)\n        BEGIN\n            SET @NewID = @NewID + 1;\n        END\n\n        -- Update the ID to the next acceptable number\n        UPDATE Comments\n        SET ID = @NewID\n        WHERE ID = @ID;\n\n        -- Reset the Identity seed to prevent future conflicts\n        DBCC CHECKIDENT ('Comments', RESEED, @NewID);\n    END\nEND;