CREATE TRIGGER trgAfterDelete\n   ON YourTargetTable\n   AFTER DELETE\n   AS\n   BEGIN\n       SET NOCOUNT ON;\n\n       INSERT INTO AuditTable (DeletedRecordID, DeletedBy, DeletedOn, SQLCommand)\n       SELECT \n           d.ID,  -- Assuming 'ID' is the primary key of the target table\n           SUSER_SNAME(),\n           GETDATE(),\n           'DELETE FROM YourTargetTable WHERE ID=' + CAST(d.ID AS NVARCHAR)\n       FROM deleted d;\n   END;