WITH T AS (\n    SELECT \n        SEQ,\n        LVL,\n        DESCR,\n        CASE WHEN LVL = 1 THEN DESCR ELSE NULL END AS L1,\n        CASE WHEN LVL = 2 THEN DESCR ELSE NULL END AS L2,\n        CASE WHEN LVL = 3 THEN DESCR ELSE NULL END AS L3\n    FROM TEST\n)\nSELECT \n    SEQ,\n    LAST_VALUE(L1 IGNORE NULLS) OVER (ORDER BY SEQ ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) AS L1,\n    LAST_VALUE(L2 IGNORE NULLS) OVER (ORDER BY SEQ ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) AS L2,\n    LAST_VALUE(L3 IGNORE NULLS) OVER (ORDER BY SEQ ROWS BETWEEN UNBOUNDED PRECEDING AND CURRENT ROW) AS L3\nFROM T\nORDER BY SEQ;