DECLARE @id INT;\n\n-- Declare a cursor to iterate through the results of the SELECT statement\nDECLARE myCursor CURSOR FOR\nSELECT id\nFROM myTable\nWHERE id = -1;\n\n-- Open the cursor\nOPEN myCursor;\n\n-- Fetch the first row\nFETCH NEXT FROM myCursor INTO @id;\n\n-- Loop through the rows\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Perform the delete operation for each result\n    DELETE FROM myOtherTable\n    WHERE someColumn = @id;\n\n    -- Fetch the next row\n    FETCH NEXT FROM myCursor INTO @id;\nEND;\n\n-- Close and deallocate the cursor\nCLOSE myCursor;\nDEALLOCATE myCursor;