-- Example table structure in each individual database\nCREATE TABLE Orders (\n    OrderId INT PRIMARY KEY,\n    OrderDate DATE,\n    Amount DECIMAL(10, 2)\n);\n\n-- Unified table structure with SiteId\nCREATE TABLE UnifiedOrders (\n    OrderId INT,\n    OrderDate DATE,\n    Amount DECIMAL(10, 2),\n    SiteId INT\n);\n\n-- Script to migrate data from individual databases to the unified database\nINSERT INTO UnifiedOrders (OrderId, OrderDate, Amount, SiteId)\nSELECT OrderId, OrderDate, Amount, 1 AS SiteId FROM database001.Orders\nUNION ALL\nSELECT OrderId, OrderDate, Amount, 2 AS SiteId FROM database002.Orders\n-- Repeat for all other databases\n;