CREATE TABLE Users (\n    user_id INT PRIMARY KEY AUTO_INCREMENT,\n    username VARCHAR(50) NOT NULL,\n    email VARCHAR(100) NOT NULL UNIQUE\n);\n\nCREATE TABLE Events (\n    event_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT,\n    title VARCHAR(100),\n    description TEXT,\n    start_datetime DATETIME,\n    end_datetime DATETIME,\n    is_recurring BOOLEAN DEFAULT FALSE,\n    FOREIGN KEY (user_id) REFERENCES Users(user_id)\n);\n\nCREATE TABLE Recurrences (\n    recurrence_id INT PRIMARY KEY AUTO_INCREMENT,\n    event_id INT,\n    recurrence_type ENUM('DAILY', 'WEEKLY', 'MONTHLY', 'YEARLY'),\n    interval INT DEFAULT 1,  -- For example, every 2 weeks\n    day_of_week ENUM('SUNDAY', 'MONDAY', 'TUESDAY', 'WEDNESDAY', 'THURSDAY', 'FRIDAY', 'SATURDAY'),  -- For weekly recurrences\n    day_of_month INT,  -- For monthly recurrences\n    month_of_year INT,  -- For yearly recurrences\n    end_date DATE,  -- NULL means it repeats forever\n    FOREIGN KEY (event_id) REFERENCES Events(event_id)\n);\n\nCREATE TABLE Exceptions (\n    exception_id INT PRIMARY KEY AUTO_INCREMENT,\n    recurrence_id INT,\n    event_id INT,\n    exception_datetime DATETIME,\n    FOREIGN KEY (recurrence_id) REFERENCES Recurrences(recurrence_id),\n    FOREIGN KEY (event_id) REFERENCES Events(event_id)  -- Exception event details\n);