SELECT ID, Name1, Name2, Name3\nFROM \n    (SELECT \n        ID, \n        Max(IIf([RowNumber]=1, [Name], Null)) AS Name1, \n        Max(IIf([RowNumber]=2, [Name], Null)) AS Name2, \n        Max(IIf([RowNumber]=3, [Name], Null)) AS Name3 \n     FROM \n         (SELECT \n             ID, \n             Name, \n             (SELECT Count(*) \n              FROM Items AS T2 \n              WHERE T2.ID = T1.ID \n              AND T2.Name <= T1.Name) AS RowNumber \n          FROM Items AS T1) \n     GROUP BY ID) AS Result;