SELECT \n  AQTORStatsID,\n  ServerName,\n  Remarks,\n  StatsBeginDateTime,\n  StatsEndDateTime,\n  [QtCPU_Average],\n  [QtCPU_TopQuintile],\n  [QtCPU_TopOnePercent],\n  [QtCl_Average],\n  [QtCl_TopQuintile],\n  [QtCl_TopOnePercent],\n  [UpdPrcStd_Average],\n  [UpdPrcStd_TopQuintile],\n  [UpdPrcStd_TopOnePercent],\n  [RcRsUPr_Average],\n  [RcRsUPr_TopQuintile],\n  [RcRsUPr_TopOnePercent]\nFROM\n(\n  SELECT \n    astats.AQTORStatsID, \n    astats.ServerName, \n    astats.Remarks, \n    astats.StatsBeginDateTime, \n    astats.StatsEndDateTime,\n    asi.PropertyName,\n    asi.AQTORStatValue\n  FROM \n    tb_rAQTORStats astats\n  INNER JOIN \n    tb_rAQTORStatsItem asi ON astats.AQTORStatsID = asi.AQTORStatsID\n) AS SourceTable\nPIVOT\n(\n  MAX(AQTORStatValue)\n  FOR PropertyName IN ([QtCPU_Average], [QtCPU_TopQuintile], [QtCPU_TopOnePercent], [QtCl_Average], [QtCl_TopQuintile], [QtCl_TopOnePercent], [UpdPrcStd_Average], [UpdPrcStd_TopQuintile], [UpdPrcStd_TopOnePercent], [RcRsUPr_Average], [RcRsUPr_TopQuintile], [RcRsUPr_TopOnePercent])\n) AS PivotTable;