ALTER FUNCTION uf_AlphaNumericIncrement\n(\n    @ID varchar(10)\n)\nRETURNS VARCHAR(10)\nAS\nBEGIN\n    DECLARE @AlphaPart VARCHAR(10);\n    DECLARE @NumPart VARCHAR(10);\n    DECLARE @NumPartInt INT;\n    DECLARE @NumPartLen INT;\n    DECLARE @Res VARCHAR(10);\n\n    -- Separate the alphabetic and numeric parts\n    SET @AlphaPart = LEFT(@ID, PATINDEX('%[0-9]%', @ID) - 1);\n    SET @NumPart = SUBSTRING(@ID, PATINDEX('%[0-9]%', @ID), LEN(@ID));\n    \n    -- Convert numeric part to integer and increment\n    SET @NumPartInt = CAST(@NumPart AS INT) + 1;\n    SET @NumPartLen = LEN(@NumPart);\n\n    -- Combine the parts back together\n    SET @Res = @AlphaPart + RIGHT(REPLICATE('0', @NumPartLen) + CAST(@NumPartInt AS VARCHAR), @NumPartLen);\n\n    RETURN @Res;\nEND