CREATE PROCEDURE GetRecords\n    @ids NVARCHAR(MAX)\nAS\nBEGIN\n    -- Split the comma-delimited string into a temporary table\n    -- Example assumes a function dbo.SplitString exists to split the string\n    DECLARE @tempTable TABLE (id INT);\n    INSERT INTO @tempTable (id)\n    SELECT value\n    FROM dbo.SplitString(@ids, ',');\n\n    -- Check the number of IDs passed\n    DECLARE @count INT;\n    SELECT @count = COUNT(*) FROM @tempTable;\n\n    IF @count = 1\n    BEGIN\n        -- If only one ID, use the = operator\n        DECLARE @singleId INT;\n        SELECT @singleId = id FROM @tempTable;\n        SELECT * FROM YourTable WHERE id = @singleId;\n    END\n    ELSE\n    BEGIN\n        -- If multiple IDs, use the IN operator\n        SELECT * FROM YourTable WHERE id IN (SELECT id FROM @tempTable);\n    END\nEND