CREATE OR REPLACE PROCEDURE game(\n    left IN tab.left%TYPE,\n    right IN tab.right%TYPE,\n    -- other parameters\n) \nIS\n    -- some local variables\nBEGIN\n    MERGE INTO tgt_table t\n    USING (SELECT left AS left_val, right AS right_val FROM dual) src\n    ON (t.some_column = src.left_val)  -- replace "some_column" with the actual column name\n    WHEN MATCHED THEN\n        UPDATE SET t.another_column = src.right_val  -- replace "another_column" with the actual column name\n    WHEN NOT MATCHED THEN\n        INSERT (t.some_column, t.another_column)  -- replace with actual column names\n        VALUES (src.left_val, src.right_val);\nEND game;